# Load Shiny package
library(shiny)

# Define UI for the Shiny app with improved design
ui <- shiny::fluidPage(
  title = "Static Shiny App showcase",
  
  
  # Custom CSS for styling
  tags$style(HTML("
    body {font-family: 'Arial', sans-serif; background-color: #f4f4f9; color: #333;}
    .title-panel {background-color: #0A6F71; padding: 30px 20px; text-align: center; color: white; font-size: 30px;}
    .sidebar {background-color: #fff; padding: 20px; border-radius: 8px; box-shadow: 0 0 10px rgba(0,0,0,0.1);}
    h3 {color: #2e4057;}
    .action-btn {background-color: #900027; border: none; color: white; padding: 10px 20px; text-align: center;
                 text-decoration: none; display: inline-block; font-size: 14px; margin-top: 20px; border-radius: 5px;
                 transition: background-color 0.3s;}
    .action-btn:hover {background-color: #f05475; cursor: pointer;}
    .fact-box {background-color: #e0f7fa; padding: 20px; border-radius: 5px; text-align: center; font-size: 16px; color: #00796b;}
    .main-panel {padding: 20px; border-radius: 8px; background-color: white; box-shadow: 0 0 10px rgba(0,0,0,0.1);}
    h2 {text-align: center; font-size: 24px; color: #2e4057;}
    p {font-size: 16px; color: #555; text-align: center;}
    .plot-container {margin-top: 30px;}
    #fact {font-style: italic;}
  ")),
  
  # Title Panel
  div(class = "title-panel", "Welcome to HealthBioCare"),
  
  # Main Layout
  sidebarLayout(
    sidebarPanel(
      div(class = "sidebar",
          h3("Discover Our Services"),
          selectInput(
            "analysisType",
            "Choose an Analysis Type:",
            choices = list("Metabolic Type" = "metabolic", "Sports" = "sports", "Stress" = "stress", "Aging" = "aging"),
            selected = "metabolic"
          ),
          actionButton("info", "Show Analysis Info", class = "action-btn"),
          br(), br(),
          div(class = "fact-box",
              h4("Quick Fact"),
              textOutput("fact")
          )
      )
    ),
    
    mainPanel(
      div(class = "main-panel",
          h2(textOutput("analysisTitle")),
          p(textOutput("analysisDescription")),
          div(class = "plot-container", plotOutput("analysisPlot"))
      )
    )
  )
)

# Define server logic
server <- function(input, output) {
  
  # Analysis details based on selection
  analysis_info <- reactive({
    switch(input$analysisType,
           metabolic = list(
             title = "Metabolic Type Analysis",
             description = "Personalized nutrition advice based on your metabolic profile."
           ),
           sports = list(
             title = "Sports Performance Analysis",
             description = "Optimize your athletic potential by understanding your bodyâ€™s genetic responses."
           ),
           stress = list(
             title = "Stress Resilience Analysis",
             description = "Learn how your body manages stress, and get recommendations for improvement."
           ),
           aging = list(
             title = "Aging and Longevity Analysis",
             description = "Discover how your genes affect aging and explore ways to slow the process."
           )
    )
  })
  
  # Render analysis title
  output$analysisTitle <- renderText({
    analysis_info()$title
  })
  
  # Render analysis description
  output$analysisDescription <- renderText({
    analysis_info()$description
  })
  
  # Generate a simple plot based on analysis type (random example data)
  output$analysisPlot <- renderPlot({
    data <- switch(input$analysisType,
                   metabolic = rnorm(100, mean = 60, sd = 15),
                   sports = rnorm(100, mean = 80, sd = 10),
                   stress = rnorm(100, mean = 50, sd = 20),
                   aging = rnorm(100, mean = 70, sd = 12)
    )
    hist(data, col = "#FF628C", border = "white", main = analysis_info()$title,
         xlab = "Test Score", ylab = "Frequency", breaks = 10)
  })
  
  # Display random health fact
  output$fact <- renderText({
    facts <- c(
      "Did you know? Your genes influence how you process certain vitamins!",
      "Did you know? Some people are genetically predisposed to handle stress better.",
      "Did you know? Your DNA can offer insights into how you age and maintain vitality.",
      "Did you know? Genetic factors can affect how efficiently you burn fat during exercise."
    )
    sample(facts, 1)
    print(sample(facts, 1))
  })
}

# Run the application 
shinyApp(ui = ui, server = server)
